// frontend/src/services/auth.js
import axios from 'axios';

// Remove '/api' from the base URL since it's included in the routes
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

export const axiosInstance = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});


export async function loginUser(email, password) {
  try {
    console.log('Attempting login at:', `${API_URL}/api/auth/login`);
    const response = await axiosInstance.post('/api/auth/login', {
      email,
      password,
    });
    return response.data;
  } catch (error) {
    console.error('Login error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status,
      url: error.config?.url
    });
    throw error;
  }
}

export async function registerUser(userData) {
  try {
    console.log('Registering at:', `${API_URL}/api/auth/register`);
    const response = await axiosInstance.post('/api/auth/register', userData);
    return response.data;
  } catch (error) {
    console.error('Registration error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status,
      url: error.config?.url
    });
    throw error;
  }
}

export async function logoutUser() {
  try {
    console.log('Logging out at:', `${API_URL}/api/auth/logout`);
    await axiosInstance.post('/api/auth/logout');
  } catch (error) {
    console.error('Logout error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status,
      url: error.config?.url
    });
    throw error;
  }
}

export async function refreshToken() {
  try {
    console.log('Refreshing token at:', `${API_URL}/api/auth/refresh-token`);
    const response = await axiosInstance.post('/api/auth/refresh-token');
    return response.data;
  } catch (error) {
    console.error('Token refresh error:', {
      message: error.message,
      response: error.response?.data,
      status: error.response?.status,
      url: error.config?.url
    });
    throw error;
  }
}
