import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Navbar = () => {
  const auth = useAuth();

  // Destructure after logging the entire auth object
  console.log('Full auth context:', auth);

  const { user, logout, isSuperAdmin } = auth;

  useEffect(() => {
    console.log('=== Navbar Mount/Update ===');
    console.log('Auth state:', {
      hasUser: !!user,
      userRole: user?.role,
      isSuperAdminFn: isSuperAdmin?.(),
      directCheck: user?.role === 'SUPERADMIN'
    });
  }, [user, isSuperAdmin]);

  // Early return if no user
  if (!user) {
    console.log('No user found in Navbar');
    return null;
  }

  // Direct role check
  const userRole = user?.role;
  const directSuperAdminCheck = userRole === 'SUPERADMIN';
  const superAdminFunctionCheck = isSuperAdmin();

  console.log('Role checks:', {
    userRole,
    directSuperAdminCheck,
    superAdminFunctionCheck
  });

  return (
    <nav className="bg-white shadow-lg">
      <div className="container mx-auto px-6 py-3">
        <div className="flex justify-between items-center">
          <Link to="/" className="text-xl font-bold">
            Tracks
          </Link>
          <div className="flex items-center space-x-4">
            <Link to="/dashboard" className="text-gray-800 hover:text-gray-600">
              Dashboard
            </Link>

            {/* Debug display */}
            <div className="text-xs text-gray-500">
              <pre>
                {JSON.stringify({
                  role: userRole,
                  direct: directSuperAdminCheck,
                  fn: superAdminFunctionCheck
                }, null, 2)}
              </pre>
            </div>

            {/* Try both conditions separately */}
            {directSuperAdminCheck && (
              <Link 
                to="/admin/companies" 
                className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
              >
                Manage (Direct)
              </Link>
            )}

            {superAdminFunctionCheck && (
              <Link 
                to="/admin/companies" 
                className="bg-purple-500 hover:bg-purple-600 text-white px-4 py-2 rounded"
              >
                Manage (Fn)
              </Link>
            )}

            <button
              onClick={logout}
              className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
            >
              Logout ({user.firstName})
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
